// 获取当前登录用户
const currUser = ref({});
const getCurrentUser = () => {
  get("/curr-user").then(result => {
    currUser.value = result.data;
    localStorage.setItem("userRole", currUser.value.userRole)
  });
}
getCurrentUser();

// 退出
const logout = () => {
  del("/logout").then(result =>{
    // 删除本地token
    removeLocalToken();
    // 替换路由
    router.replace("/login");
    tip.success("您已退出系统！");
  });
}

// 获取所有权限
const menuList = ref([]);
const getMenuList = () => {
  get("/user/auth-list").then(result => {
    menuList.value = result.data;
  });
}
getMenuList();

